import { getUserProfile, getUniqueUsers } from "@/lib/user";
import Link from "next/link";
import Image from "next/image";
import { notFound } from 'next/navigation';

import popHopLogo from "../../assets/pop-hop.svg";
import ShareButton from "../components/ShareButton";
import PhoneNumber from "../components/PhoneNumber";

type PlatformData = {
  platform: "Github" | "GitLab" | "Bitbucket" | string;
  userName: string;
};

type User = {
  name: string;
};

export default async function Page({ params }: { params: { username: string } }) {
  const username = params.username;

  if (!username) {
    return notFound();
  }

  const profile = await getUserProfile(username)

  if (!profile) {
    notFound();
  }

  return (
    <section className="h-screen overflow-y-auto pb-16 bg-primary-foreground">
      <div className="flex flex-col items-center justify-start mx-auto max-w-150">
        <ShareButton />
        {profile.avatar && <Image src={profile.avatar} alt={`${profile.name} Avatar`} priority width={400} height={424} className="rounded-full" />}
        <h2 className="text-pf-name text-xl font-medium mt-12">{profile.name}</h2>
        <h2 className="my-2 text-pf-name text-base font-normal">{profile.aboutUser}</h2>
        <div className="flex items-center justify-center gap-2 h-9 w-full">
          <Link href={`mailto:${profile.email}`} aria-label="Contact via email">
            {emailIcon}
          </Link>
          <PhoneNumber contactCountry={profile?.contactCountry} contactNumber={profile?.contactPhoneNumber} />
        </div>

        {profile?.socialLinks?.length > 0 && (
          <>
            <h3 className="text-pf-name text-lg font-semibold mt-6 mb-4">My Social Links</h3>

            <div className="space-y-4 w-full">
              {profile?.socialLinks?.map((platform: PlatformData, index: number) => (
                <Link key={index} className="rounded-md bg-sc-link h-16.5 p-2 flex items-center gap-1 w-full" href={platform?.userName || '#'} target="_blank" rel="noopener noreferrer">
                  <div className="w-14 flex items-center justify-center gap-2">
                    {platform.platform === "Youtube" && youtubeIcon}
                    {platform.platform === "TikTok" && tiktokIcon}
                    {platform.platform === "Instagram" && instagramIcon}
                    {platform.platform === "X (Twitter)" && twitterIcon}
                    {platform.platform === "VK.com" && vkIcon}
                    {platform.platform === "Pinterest" && pinterestIcon}
                    {platform.platform === "Vimeo" && vimeoIcon}
                    {platform.platform === "Discord" && discordIcon}
                    {platform.platform === "Facebook" && facebookIcon}
                    {platform.platform === "We Chat" && wechatIcon}
                    {platform.platform === "Rakuten Viber" && viberIcon}
                    {platform.platform === "WhatsApp" && whatsappIcon}
                    {platform.platform === "Snapchat" && snapchatIcon}
                    {platform.platform === "Notion" && notionIcon}
                    {platform.platform === "Behance" && behanceIcon}
                    {platform.platform === "Dribble" && dribbbleIcon}
                    {platform.platform === "Flickr" && flickrIcon}
                    {platform.platform === "LinkedIn" && linkedinIcon}
                    {platform.platform === "Twitch" && twitchIcon}
                    {platform.platform === "Reddit" && redditIcon}
                    {platform.platform === "Slack" && slackIcon}
                    {platform.platform === "Apple Music" && appleMusicIcon}
                    {platform.platform === "Youtube Music" && youtubeMusicIcon}
                    {platform.platform === "Sound Cloud" && soundcloudIcon}
                    {platform.platform === "Shazam" && shazamIcon}
                    {platform.platform === "Spotify" && spotifyIcon}
                    {platform.platform === "PayPal" && paypalIcon}
                    {platform.platform === "Stripe" && stripeIcon}
                    {platform.platform === "Telegram" && telegramIcon}
                    {platform.platform === "Github" && githubIcon}
                    {platform.platform === "Gitlab" && gitlabIcon}
                    {platform.platform === "Figma" && figmaIcon}
                  </div>
                  <span className="text-base font-medium text-pf-name">
                    {platform?.platform}
                  </span>
                </Link>
              ))}
            </div>
          </>
        )}

        <div className="mt-10 flex items-center justify-center">
          <Image src={popHopLogo} alt="Pophop Logo" priority />
        </div>
      </div>
    </section>
  );
}

export async function generateStaticParams(): Promise<{ username: string }[]> {
  const users: User[] = (await getUniqueUsers()) ?? [];
 
  return users.map((user) => ({
    username: user.name,
  }));
}

